var kloutApplications = kloutApplicationManager.GetKloutApplicationsByContainer("/sitecore/system/Social/Klout/Applications").ToList();
if (kloutApplications.Any())
{
  foreach (var kloutApplication in kloutApplications)
  {
    this.ShowKloutApplication(kloutApplication);
  }
}

var kloutApplications = kloutApplicationManager.GetKloutApplicationsByContainer("/sitecore/system/Social/Klout/Applications", true).ToList();
if (kloutApplications.Any())
{
  foreach (var kloutApplication in kloutApplications)
  {
    this.ShowKloutApplication(kloutApplication);
  }
}

