if (kloutProfileManager.SourceNetworkSupported("Twitter"))
{
  var sourceNetworkIdentity = new SourceNetworkIdentity
  {
    NetworkAccountId = "582593984",
    NetworkName = "Twitter"
  };

  var kloutId = await kloutProfileManager.RetrieveKloutIdAsync(kloutApplicationId, sourceNetworkIdentity);
  if (!string.IsNullOrEmpty(kloutId))
  {
    Console.WriteLine("Klout Id: {0}", kloutId);
  }
}

